mapper.select:
SELECT boardId, title, content, writer, DATE_FORMAT(boardDatetime, '%Y-%m-%d %H:%i:%s') AS boardDatetime, replyCnt
FROM bbs_board
WHERE boardId = #{boardId};

mapper.update:
UPDATE bbs_board
SET title           = #{title},
    content         = #{content},
    boardUpDatetime = #{boardUpDatetime}
WHERE boardId = #{boardId};

UPDATE bbs_board
SET viewCnt = viewCnt + 1
WHERE boardId = #{boardId};
