mapper.update:
UPDATE bbs_board
SET title           = #{title},
    content         = #{content},
    boardUpDatetime = #{boardUpDatetime}
WHERE boardId = #{boardId};

mapper.select:
SELECT row_number() over (ORDER BY boardId) AS no, boardId, title, writer, viewCnt, replyCnt, DATE_FORMAT(boardDatetime, '%Y-%m-%d %H:%i:%s') AS boardDatetime FROM bbs_board WHERE 1=1 /*true 뜻 함*/
AND title LIKE CONCAT('%',#{keyword},'%') AND content LIKE CONCAT('%',#{keyword},'%') AND writer LIKE CONCAT('%',#{keyword},'%')
ORDER BY no DESC limit #{startIndex}, #{cntPerPage};

mapper.update:
UPDATE bbs_board
SET viewCnt = viewCnt + 1
WHERE boardId = #{boardId};

mapper.select:
SELECT fileId, boardId, originFilename, saveFilename, fileSize, createDatetime, delYn
FROM bbs_file
WHERE boardId = #{boardId}
  AND fileSize > 0;

SELECT originFilename
FROM bbs_file
WHERE fileId = #{fileId};
